import logo from './logo.svg'
import * as Tone from 'tone'
import './App.css'

function App() {

  const synth = ''

  const Piano = ({synth}) => {
    const whiteKeys = ['C', 'D', 'E', 'G', 'A', 'B', 'C']
    const blackKeys = ['C#', 'D#', 'F', 'A#', 'B#'];

    return (
      <div className="piano-roll">
        <div className="black-keys">
          {blackKeys.map((note, index) => (
            <PianoKey key={index} note={note} synth={synth} className="black-key key" />
          ))}
        </div>
        <div className="white-keys">
          {whiteKeys.map((note, index) => (
            <PianoKey key={index} note={note} synth={synth} className="white-key key" />
          ))}
        </div> 
      </div>
    )
  }

  const PianoKey = ({ synth, note }) => {
    const playNote = () => {
        synth.triggerAttackRelease(note, '8n');
    }

    return (
      <button onClick={playNote} style={keyStyle}>
          {note}
      </button>
    );
};


  const handleButtonClick = async () => {
    await Tone.start()
    synth = new Tone.Synth().toDestination()
    console.log('audio is ready')
  }

  return (
    <div className="App">
      <header className="App-header">
        <Piano synth={synth} />
        <button onClick={handleButtonClick}>Click me</button>
      </header>
    </div>
  )
}

export default App
