import logo from './logo.svg'
import * as Tone from 'tone'
import './App.css'

function App() {

  // async startTone() => {
  //   await Tone.start()
  //   console.log('audio is ready')
  // }

  const Piano = () => {
    const whiteKeys = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'A#', 'B']
    const blackKeys = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'A#', 'B'];

    return (
      <>
        <section className="black-keys">
          {notes.map((note, index) => (
            <PianoKey key={index} note={note} isBlack={note.includes('#')} />
          ))}
        </section>

      </>
    )
  }

  const PianoKey = ({ note, isBlack }) => {
    const playNote = () => {
        const synth = new Tone.Synth().toDestination();
        synth.triggerAttackRelease(note, '8n');
    };

    const keyStyle = isBlack ? { backgroundColor: 'black', color: 'white' } : {};

    return (
        <button onClick={playNote} style={keyStyle}>
            {note}
        </button>
    );
};


  const handleButtonClick = async () => {
    await Tone.start()
    console.log('Button was clicked!');

    //create a synth and connect it to the main output (your speakers)
    const synth = new Tone.Synth().toDestination();

    //play a middle 'C' for the duration of an 8th note
    synth.triggerAttackRelease("C6", "8n");
    // Additional async operations can go here
  };

  return (
    <div className="App">
      <header className="App-header">
        <Piano/>
        <button onClick={handleButtonClick}>Click me</button>
      </header>
    </div>
  )
}

export default App
